class Pendu():    """docstring for Pendu"""    def __init__(self):        """Initialisation des fonctions"""        self.words = self.get_words()        self.play = self.play_game()    def get_words(self):        """ Cette fonction contient une bibliotheque random        qui permet de choisir des mots aleatoire et la liste de mots."""        from random import choice        get_liste = [        "manger",        "dormir",        "frapper",        "amour",        "reviser",        "ranger",        "allumer"        ]        return choice(get_liste)    def recup_lettre(self):        """Cette fonction récupère une lettre saisie par        l'utilisateur. Si la chaîne récupérée n'est pas une lettre,        on appelle récursivement la fonction jusqu'à obtenir une lettre"""        lettre = input("Tapez une lettre : ")        lettre = lettre.lower()        if len(lettre)>1 or not lettre.isalpha():            print("Vous n'avez pas saisi une lettre valide.")        else:            return lettre    def recup_mot_masque(self, mot_complet, lettres_trouvees):        """Cette fonction renvoie un mot masqué tout ou en partie, en        fonction :        - du mot d'origine (type str)        - des lettres déjà trouvées (type list)        On renvoie le mot d'origine avec des * remplaçant les lettres que        l'on        n'a pas encore trouvées."""                mot_masque = ""        for lettre in mot_complet:            if lettre in lettres_trouvees:                mot_masque += lettre            else:                mot_masque += "*"        return mot_masque    def play_game(self):        """Cette fonction sert a lancer le jeu.         Juste en bas il y a differentes         declaration de variable qui nous seront         utile pour le bon fonctionnement du jeu"""        mot_a_trouver = self.get_words()        lettres_trouvees = []        mot_trouve = self.recup_mot_masque(mot_a_trouver, lettres_trouvees)        nb_chanc